  } return($self);}
sub loadf0nt{my $self = shift;my $f8fn = shift;my $ftyp = shift;my($bgin,$ennd,$rndx); # even though method called loadF0NT, type might be other
  if(!defined($ftyp) && defined($f8fn)){$ftyp = $f8fn;$ftyp =~ s/^.*\.(\w+)$/$1/;} # load f0nt type from filename extension if no parameter came in
  if(defined($f8fn) && -e $f8fn && $ftyp =~ /^(f0nt|psf|fnt)$/){ # make sure existing file is of supported type
    open(F8FN,'<',$f8fn);
    if    ($ftyp eq 'f0nt'){
      while(<F8FN>){
        if    (/^(f8.*)$/){           $self->{'f0nthedr'} = $1; # store the header string in case it's needed l8r
        }elsif(/^([0123]?[0-9A-Za-z._])-([0123]?[0-9A-Za-z._])$/){($bgin,$ennd,$rndx)=(b10($1),b10($2),0);
        }else{my @roww = split(/\s/,$_);
          for my $fndx ($bgin..$ennd){$self->{'f0nt'    }[$fndx][$rndx] = $roww[$fndx-$bgin];} $rndx++;
        }}
    }elsif($ftyp =~ /^(psf|fnt)$/){my @fdat = ();while(<F8FN>){push(@fdat,unpack('C*',$_));}
      my @hedr =('s','4','0','G');if($ftyp ne 'fnt'){for(0..3){$hedr[$_] = b64(shift(@fdat));}} # assume default header info for .fnt files
      my $hite = b10($hedr[3]);       $self->{'f0nthedr'} = "f80p @hedr";
      for       my $indx(0..256){ # preload all d8a with blank bits then substr in approprE8 '#'
        for     my $rowe(0..($hite-1)){$self->{'f0nt'}[$indx][$rowe] = '--------';}}
      for       my $indx(0..15){ # loop thru binary d8a && construct $self->{'f0nt'} like above
        for     my $rowe(0..($hite-1)){
          for   my $cndx(0..15){
            for my $colm(reverse(0..7)){
              if($fdat[($indx*16+$cndx)*$hite+$rowe] >= 2**$colm){
                substr($self->{'f0nt'}[$indx*16+$cndx][$rowe],(7-$colm),1,'#');
                 $fdat[($indx*16+$cndx)*$hite+$rowe] -= 2**$colm;}}}}}
    }                                 $self->{'f0ntname'} = $f8fn; # also store the filename just loaded (which may include path too)
    close(F8FN);                      $self->{'f0nttype'} = $ftyp; #   && store the filetype too (should usually be same as f0ntname extension)
  }else{ # if new called empty, load Default f8 tektite f0nt
    for(split(/\n/,$df8f)){
      if    (/^(f8.*)$/){             $self->{'f0nthedr'} = $1; # store the header string in case it's needed l8r
      }elsif(/^([0123]?[0-9A-Za-z._])-([0123]?[0-9A-Za-z._])$/){($bgin,$ennd,$rndx)=(b10($1),b10($2),0);
      }else{my @roww = split(/\s/,$_);
        for my $fndx ($bgin..$ennd){  $self->{'f0nt'    }[$fndx][$rndx] = $roww[$fndx-$bgin];} $rndx++;
      }}                              $self->{'f0ntname'} = 'tektite.f0nt'; # store default name even though file doesn't actually exist to load from
                                      $self->{'f0nttype'} =         'f0nt';
  }}
sub loadpal8{my $self = shift;my $f8pn = shift;
  if(-e $f8pn){
    open(F8PN,'<',$f8pn);while(<F8PN>){
      if    (/^pal8\s*(.*)$/){        $self->{'pal8'    } = $1;    # store the pal8d8a (right now multiple pal8 lines clobber leaving only last line's d8a)
      }} close(F8PN);                 $self->{'pal8name'} = $f8pn; # also store the filename just loaded (which may include path too)
  }}
sub savef0nt{my $self = shift;my $f8fn = shift || 'dflt.f0nt';my $ftyp = shift; # attempt 2 save lossless roundtrip (should error check if!any loaded)
  if(!defined($ftyp) && defined($f8fn)){$ftyp = $f8fn;$ftyp =~ s/^.*\.(\w+)$/$1/;} # load f0nt type from filename extension if no parameter came in
  if($ftyp =~ /^(f0nt|psf|fnt)$/){#|pcf|bdf|fon|otf|sit|hqx|pfa|sfd|ttf
    my @hdat = split(/\s+/,$self->{'f0nthedr'});shift(@hdat); # get rid of first field 'f80p'
    open(F8FN,'>',$f8fn); # might want to check if exists to confirm overwrite?
    if    ($ftyp eq 'f0nt'){my $hite = b10($hdat[3]);print F8FN $self->{'f0nthedr'},"\n";
      for       my $indx(0..15){print F8FN b64($indx*16).'-'.b64($indx*16+15)."\n";
        for     my $rowe(0..($hite-1)){
          for   my $cndx(0..15){print F8FN $self->{'f0nt'}[$indx*16+$cndx][$rowe];print F8FN ' ' if($cndx < 15);}print F8FN "\n";}}
    }elsif($ftyp =~ /^(psf|fnt)$/){my $hite = @{$self->{'f0nt'}[0]}; # first char should be array of hite rows
      if($ftyp eq 'psf'){print F8FN pack('C',b10($_)) for(@hdat);} # only psf gets header
      for             (0..255){
        for   my $rowe(0..b10($hdat[3])-1){my $rbyt = 0;
          for my $colm(0..7){ # build Row byte d8a by columns
            if(substr($self->{'f0nt'}[$_][$rowe],7-$colm,1) !~ /^[-]$/){$rbyt += 2**$colm;}} # l8r might need to ck for more off-bit chars
          print F8FN pack('C',$rbyt);}}
    } close(F8FN);
  }}
